import React, { useState } from 'react';

const PromotionCards = ({ teste }) => {
    const [adicionar, setAdicionar]= useState(false);

    const toggle = () =>{
        setAdicionar(!adicionar)
    }
    return ( 
        <>

        <button onClick={toggle}>{adicionar ? 'X' : 'colocar'}</button>
        <h1 className="marcos">marcos</h1>
        {adicionar && (
            <h1
            style={{color:'red'}}>{"marcos"}</h1>
        )}


        </>
     );
}

export default PromotionCards;
